<?phpnamespace TDDV\CommandeBundle\Entity;use Doctrine\ORM\Mapping as ORM;/** * Client * * @ORM\Table(name="tddv_client") * @ORM\Entity(repositoryClass="TDDV\CommandeBundle\Entity\ClientRepository") */class Client{    /**     * @var integer     *     * @ORM\Column(name="id", type="integer")     * @ORM\Id     * @ORM\GeneratedValue(strategy="AUTO")     */    private $id;    /**     * @var string     *     * @ORM\Column(name="Nom", type="string", length=255)     */    private $nom;    /**     * @var string     *     * @ORM\Column(name="Prenom", type="string", length=255)     */    private $prenom;    /**     * Get id     *     * @return integer      */    public function getId()    {        return $this->id;    }    /**     * Set nom     *     * @param string $nom     * @return Client     */    public function setNom($nom)    {        $this->nom = $nom;            return $this;    }    /**     * Get nom     *     * @return string      */    public function getNom()    {        return $this->nom;    }    /**     * Set prenom     *     * @param string $prenom     * @return Client     */    public function setPrenom($prenom)    {        $this->prenom = $prenom;            return $this;    }    /**     * Get prenom     *     * @return string      */    public function getPrenom()    {        return $this->prenom;    }        public function getInfos(){        return $this->nom.' '.$this->prenom;    }}