<?phpnamespace TDDV\CommandeBundle\Entity;use Doctrine\ORM\Mapping as ORM;/** * Coffret * *  * @ORM\Entity(repositoryClass="TDDV\CommandeBundle\Entity\CoffretRepository") */class Coffret extends Produit{        /**     * @ORM\OneToMany(targetEntity="TDDV\CommandeBundle\Entity\CoffretMessage", mappedBy="coffret", cascade={"persist"})     */    private $coffretMessages;    /**     * @var \DateTime     *     * @ORM\Column(name="DateDebut", type="date")     */    private $dateDebut;    /**     * @var \DateTime     *     * @ORM\Column(name="DateFin", type="date")     */    private $dateFin;             /**     * @var string     *     * @ORM\Column(name="Theme", type="string", length=255)     */    private $theme;    /**     * @var integer     *     * @ORM\Column(name="NombreCd", type="integer")     */    private $nombreCd;    /**     * Constructor     */    public function __construct()    {        $this->coffretMessages = new \Doctrine\Common\Collections\ArrayCollection();    }        /**     * Set dateDebut     *     * @param \DateTime $dateDebut     * @return Coffret     */    public function setDateDebut($dateDebut)    {        $this->dateDebut = $dateDebut;            return $this;    }    /**     * Get dateDebut     *     * @return \DateTime      */    public function getDateDebut()    {        return $this->dateDebut;    }    /**     * Set dateFin     *     * @param \DateTime $dateFin     * @return Coffret     */    public function setDateFin($dateFin)    {        $this->dateFin = $dateFin;            return $this;    }    /**     * Get dateFin     *     * @return \DateTime      */    public function getDateFin()    {        return $this->dateFin;    }    /**     * Set theme     *     * @param string $theme     * @return Coffret     */    public function setTheme($theme)    {        $this->theme = $theme;            return $this;    }    /**     * Get theme     *     * @return string      */    public function getTheme()    {        return $this->theme;    }    /**     * Set nombreCd     *     * @param integer $nombreCd     * @return Coffret     */    public function setNombreCd($nombreCd)    {        $this->nombreCd = $nombreCd;            return $this;    }    /**     * Get nombreCd     *     * @return integer      */    public function getNombreCd()    {        return $this->nombreCd;    }        public function getInfos(){        return $this->theme;    }    /**     * Add coffretMessages     *     * @param \TDDV\CommandeBundle\Entity\CoffretMessage $coffretMessages     * @return Coffret     */    public function addCoffretMessage(\TDDV\CommandeBundle\Entity\CoffretMessage $coffretMessages)    {        $this->coffretMessages[] = $coffretMessages;            return $this;    }    /**     * Remove coffretMessages     *     * @param \TDDV\CommandeBundle\Entity\CoffretMessage $coffretMessages     */    public function removeCoffretMessage(\TDDV\CommandeBundle\Entity\CoffretMessage $coffretMessages)    {        $this->coffretMessages->removeElement($coffretMessages);    }    /**     * Get coffretMessages     *     * @return \Doctrine\Common\Collections\Collection      */    public function getCoffretMessages()    {        return $this->coffretMessages;    }}