<?phpnamespace TDDV\CommandeBundle\Entity;use Doctrine\ORM\Mapping as ORM;/** * CommandeProduit * * @ORM\Table(name="tddv_commande_produit") * @ORM\Entity(repositoryClass="TDDV\CommandeBundle\Entity\CommandeProduitRepository") */class CommandeProduit{    /**      * @ORM\Id      * @ORM\ManyToOne(targetEntity="TDDV\CommandeBundle\Entity\Commande", inversedBy="commandeproduits")      */    private $commande;    /**      * @ORM\Id      * @ORM\ManyToOne(targetEntity="TDDV\CommandeBundle\Entity\Produit")      */    private $produit;    /**     * @var integer     *     * @ORM\Column(name="Quantite", type="integer")     */    private $quantite;    /**     * @var boolean     *     * @ORM\Column(name="imprime", type="boolean")     */    private $imprime;    /**     * @var boolean     *     * @ORM\Column(name="Grave", type="boolean")     */    private $grave;    /**     * Set quantite     *     * @param integer $quantite     * @return CommandeProduit     */    public function setQuantite($quantite)    {        $this->quantite = $quantite;            return $this;    }    /**     * Get quantite     *     * @return integer      */    public function getQuantite()    {        return $this->quantite;    }    /**     * Set imprime     *     * @param boolean $imprime     * @return CommandeProduit     */    public function setImprime($imprime)    {        $this->imprime = $imprime;            return $this;    }    /**     * Get imprime     *     * @return boolean      */    public function getImprime()    {        return $this->imprime;    }    /**     * Set grave     *     * @param boolean $grave     * @return CommandeProduit     */    public function setGrave($grave)    {        $this->grave = $grave;            return $this;    }    /**     * Get grave     *     * @return boolean      */    public function getGrave()    {        return $this->grave;    }    /**     * Set commande     *     * @param \TDDV\CommandeBundle\Entity\Commande $commande     * @return CommandeProduit     */    public function setCommande(\TDDV\CommandeBundle\Entity\Commande $commande)    {        $this->commande = $commande;            return $this;    }    /**     * Get commande     *     * @return \TDDV\CommandeBundle\Entity\Commande      */    public function getCommande()    {        return $this->commande;    }    /**     * Set produit     *     * @param \TDDV\CommandeBundle\Entity\Produit $produit     * @return CommandeProduit     */    public function setProduit(\TDDV\CommandeBundle\Entity\Produit $produit)    {        $this->produit = $produit;            return $this;    }    /**     * Get produit     *     * @return \TDDV\CommandeBundle\Entity\Produit      */    public function getProduit()    {        return $this->produit;    }}