<?phpnamespace TDDV\CommandeBundle\Entity;use Doctrine\ORM\Mapping as ORM;/** * Predication * @ORM\Entity(repositoryClass="PredicationRepository") */class Predication extends Produit{    /**     * @ORM\OneToOne(targetEntity="Message", cascade={"persist"})     * @ORM\JoinColumn(nullable=false)     */    private $message;        function __construct() {            $this->setPrix(2);           }        /**     * Set message     *     * @param Message $message     * @return Predication     */    public function setMessage(Message $message)    {        $this->message = $message;            return $this;    }    /**     * Get message     *     * @return Message      */    public function getMessage()    {        return $this->message;    }    public function getInfos(){        return $this->message->getTitre();    }}