<?phpnamespace TDDV\CommandeBundle\Entity;use Doctrine\ORM\Mapping as ORM;/** * Produit * * @ORM\Table(name="tddv_produit") * @ORM\Entity(repositoryClass="TDDV\CommandeBundle\Entity\ProduitRepository") * @ORM\InheritanceType("SINGLE_TABLE") * @ORM\DiscriminatorColumn(name="type", type="string") * @ORM\DiscriminatorMap( {"predication" = "TDDV\CommandeBundle\Entity\Predication", "coffret" = "TDDV\CommandeBundle\Entity\Coffret"} ) *  */abstract class Produit{    /**     * @var integer     *     * @ORM\Column(name="id", type="integer")     * @ORM\Id     * @ORM\GeneratedValue(strategy="AUTO")     */    private $id;    /**     * @var float     *     * @ORM\Column(name="Prix", type="decimal")     */    private $prix;    /**     * Get id     *     * @return integer      */    public function getId()    {        return $this->id;    }    /**     * Set prix     *     * @param float $prix     * @return Produit     */    public function setPrix($prix)    {        $this->prix = $prix;            return $this;    }    /**     * Get prix     *     * @return float      */    public function getPrix()    {        return $this->prix;    }}